? UniV3WrappedLibMockup.MAX_SQRT_RATIO - 1 // Token0 -> Token1

: UniV3WrappedLibMockup.MIN_SQRT_RATIO + 1, // Token1 -> Token0

abi.encode(address(this))

);

}

